method.comment:
/**
        *	Helper to make the URL to approve/reject the ticket
        */

method.code:
public function MakeReplyUrl($sContactClass, $iContactId, $bFromGUI = true)
{
	$sPassCode = $this->GetContactPassCode($sContactClass, $iContactId);
	if (is_null($sPassCode))
	{
		$sReplyUrl = null;
	}
	else
	{
		if ($bFromGUI)
		{
			$sToken = $this->GetKey().'-'.$this->Get('current_step').'-'.$sContactClass.'-'.$iContactId.'-'.$sPassCode;
			$sReplyUrl = utils::GetAbsoluteUrlModulesRoot().'approval-base/approve.php?token='.$sToken;
			$sReplyUrl .= '&from=object_details';
		}
		else
		{
			// Redirect EVERY user to the portal
		    // TODO : Adjust depending on the implementation! The current code will make an url for the portal instance of the user which is ok
			// in most case when it has only one or/and its portal ID is no longer "itop-portal" (eg. custom portals). BUT it is NOT ok if
			// the user is allowed multiple portals and the approval brick is not on the first one!
			$sPortalId = 'itop-portal';
			$aAllowedPortals = UserRights::GetAllowedPortals();
			if(!empty($aAllowedPortals))
			{
				$sPortalId = $aAllowedPortals[0]['id'];
			}
			$sReplyUrl = utils::GetAbsoluteUrlModulesRoot() . $sPortalId . '/index.php/approval/approvals';
		}
	}
	return $sReplyUrl;
}
